	)ENGINE=MYISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
	
	/*m s ln lm bi thi*/
	SELECT COUNT(*) FROM taker_test_map WHERE test_id=p_test_id INTO @takecount;
	
	/*Thm cc rows km theo total vo _report_7data*/
	insert into _report_7data(
